Drupal.behaviors.modal={attach(e){document.querySelectorAll(".pds-modal").forEach(e=>{const d=e.id,t=d.substring(d.indexOf("modal_")+6),n=document.getElementById(`modalBtn_${t}`),l=document.getElementById(`modal_${t}`),a=l.querySelector(".pds-close-btn");n.addEventListener("click",e=>{e.preventDefault(),l.style.display="block",n.disabled=!0}),a.addEventListener("click",e=>{e.preventDefault(),l.style.display="none",n.disabled=!1}),window.addEventListener("keydown",function(e){"Escape"===e.key&&(l.style.display="none",n.disabled=!1)}),window.onclick=function(d){d.target==e&&(l.style.display="none",n.disabled=!1)}})}};