Drupal.behaviors.modal={attach(e){document.querySelectorAll(".modal").forEach(e=>{const t=e.id,d=t.substring(t.indexOf("--")+2),n=document.getElementById(`modalBtn--${d}`),l=document.getElementById(`modal--${d}`),a=l.querySelector(".close-btn");n.addEventListener("click",e=>{e.preventDefault(),l.style.display="block",n.disabled=!0}),a.addEventListener("click",e=>{e.preventDefault(),l.style.display="none",n.disabled=!1}),window.addEventListener("keydown",function(e){"Escape"===e.key&&(l.style.display="none",n.disabled=!1)}),window.onclick=function(t){t.target==e&&(l.style.display="none",n.disabled=!1)}})}};